import React from 'react'
import { connect } from 'react-redux'
import { firestoreConnect } from 'react-redux-firebase'
import { compose } from 'redux'

const ProjectDetails = (props) => {
    const id = props.match.params.id;
    console.log(props);
    return (
        <div className="container section project-details">
            <div className="card z-depth-0">
                <div className="card-content">
                    <span className="card-title">Project Title - {id}</span>
                    <p>Lorem ipsum dolor sit amet consectetur adipisicing elit. Nam aut soluta officiis 
                        aperiam repellat atque modi placeat quae, illum, dolorum ipsa, harum inventore 
                        ab distinctio obcaecati a libero accusantium quos!</p>
                </div>
                <div className="card-action grey lighten-4 grey-text">
                    <div>Posted by TC</div>
                    <div>30th July, 11pm</div>
                </div>
            </div>
        </div>
    )
}

const mapStateToProps = (state, ownProps) => {
    // console.log(state);
    const id = ownProps.match.params.id
    const projects = state.firestore.data.projects;
    const project = projects ? projects[id] : null
        return{
        project: project
    }
}
export default compose(
    connect(mapStateToProps),
    firestoreConnect([
        { collection: 'projects' }
    ])
)(ProjectDetails)
